class Mapper{constructor(mapID,latitude,longitude){if(typeof mapID!=='string'){throw new TypeError("MapID needs to be of type [String]")}
if(typeof latitude!=='number'||typeof longitude!=='number'){throw new TypeError("Expected a floating point for latitude and longitude")}
var element=document.getElementById(mapID);if(element!=null){this.styleElement(element)}
else{var newMapDiv=document.createElement("div");newMapDiv.setAttribute("id",mapID);document.body.appendChild(newMapDiv);element=document.getElementById(mapID);this.mapElement=element;this.styleElement(element)}
this.latitude=latitude;this.longitude=longitude;this.mapElement=element}
styleElement(){this.mapElement.setAttribute("style","width:100%; margin:0px; height:147px; border-right : 0px;")}
createMarkerMap(){var mapInstance;var latlng=new google.maps.LatLng(this.latitude,this.longitude);mapInstance=new google.maps.Map(this.mapElement,{center:latlng,zoom:10});var marker=new google.maps.Marker({map:mapInstance,position:latlng})}
static showMultipleMapMarkers(mapID,addresses){var element;if(typeof mapID!=='string'){throw new TypeError('Expected a string')}
else{element=document.getElementById(mapID);if(element==null){throw new ReferenceError("DOM Id does not exist. Create an element with an ID")}}
if(typeof addresses!=='object'){throw new TypeError('Expected an array. You provided '+typeof addresses)}
if(addresses.length==0){throw new ReferenceError("Object length must be greater than 0")}
var map=new google.maps.Map(element,{zoom:4,center:new google.maps.LatLng(39.774769,-101.337891),mapTypeId:google.maps.MapTypeId.ROADMAP});element.setAttribute("style","width:100%; margin:0px; height:700px; !important; border-right : 0px;");let infoWindow=new google.maps.InfoWindow();let marker;for(var counter=0;counter<addresses.length;counter++){marker=new google.maps.Marker({position:new google.maps.LatLng(addresses[counter][1],addresses[counter][2]),map:map});google.maps.event.addListener(marker,'mouseover',(function(marker,counter){return function(){infoWindow.setContent("<strong>Address 1 : </strong>"+addresses[counter][0]+"<br/><strong>Latitude</strong> : "+addresses[counter][1]+"<br/> <strong>Longitude</strong> : "+addresses[counter][2]);infoWindow.open(map,marker)}})(marker,counter));google.maps.event.addListener(marker,'mouseout',(function(marker,counter){return function(){infoWindow.close()}})(marker,counter))}}}